import React, { Component } from "react";
import "./Repositories.css";
import axios from "axios";

export class Repositories extends Component {
  constructor() {
    super();
    this.state = {
      repos: []
    };
  }

  componentDidMount() {
    this.performSearch();
  }

  performSearch = () => {
    axios
      .get("https://api.github.com/users/ViniciusAlvesC/repos")
      .then(response => {
        const newRepo = [];
        response.data.map(repo => {
          return newRepo.push(repo);
        });

        this.setState({
          repos: newRepo
        });
      })
      .catch(error => {
        console.log("Error fetching and parsing data", error);
      });
  };

  renderCards = () => {
    const cards = this.state.repos.map(repo => {
      return (
        <div className="repositorie" key={repo.id}>
          <a href={repo.html_url} target="_blank" rel="noopener noreferrer">
            <div className="repositorie_bg"></div>
          </a>
          <div className="repo_info">
            <a href={repo.html_url} target="_blank" rel="noopener noreferrer">
              <h3>{repo.name}</h3>
            </a>
            <div className="tags">
              <span className="tag">{repo.language}</span>
            </div>
            <p className="repo_desc">{repo.description}</p>
          </div>
        </div>
      );
    });
    return cards;
  };

  render() {
    return (
      <div className="container">
        <div className="repositories">{this.renderCards()}</div>
      </div>
    );
  }
}

export default Repositories;
